import json import pandas as pd from datasets import Dataset, load_dataset from transformers import AutoTokenizer def prepare_alpaca_dataset(data_path, tokenizer, max_length=2048): """Prepare Alpaca format dataset""" # Load data with open(data_path, 'r', encoding='utf-8') as f: data = json.load(f) def format_prompt(example): if example['input']: prompt = f"### Instruction:
{example['instruction']}

### Input:
{example['input']}

### Response:
" else: prompt = f"### Instruction:
{example['instruction']}

### Response:
" full_text = prompt + example['output'] return {"text": full_text} # Format data formatted_data = [format_prompt(item) for item in data] dataset = Dataset.from_list(formatted_data) # Tokenize def tokenize_function(examples): return tokenizer( examples["text"], truncation=True, padding=False, max_length=max_length, return_overflowing_tokens=False, ) tokenized_dataset = dataset.map( tokenize_function, batched=True, remove_columns=dataset.column_names ) return tokenized_dataset # Usage example tokenizer = AutoTokenizer.from_pretrained("Qwen/Qwen2-7B") tokenizer.pad_token = tokenizer.eos_token train_dataset = prepare_alpaca_dataset("train_data.json", tokenizer) eval_dataset = prepare_alpaca_dataset("eval_data.json", tokenizer)
